import { stringify } from "./utils.js";
import { registerReader, registerWriter } from "./format-graph.js";
export function getCoreTypesReader() {
    return {
        kind: 'ct',
        read(source) {
            const doc = JSON.parse(source);
            return {
                data: doc,
                convertedTypes: doc.types.map(({ name }) => name),
                notConvertedTypes: [],
            };
        },
    };
}
export function getCoreTypesWriter() {
    return {
        kind: 'ct',
        write(doc) {
            return {
                data: stringify(doc),
                convertedTypes: doc.types.map(({ name }) => name),
                notConvertedTypes: [],
            };
        },
    };
}
registerReader(getCoreTypesReader());
registerWriter(getCoreTypesWriter());
